mapper.select:
SELECT boardType, boardCategory, regdate, viewCount, likeCount
FROM POST_MINI
WHERE postNo = #{postNo}

mapper.update:
	    UPDATE POST_MINI 
    	SET VIEW_COUNT = VIEW_COUNT + 1 
    	WHERE POST_NO = #{postNo}

UPDATE POST_MINI
SET LIKE_COUNT = LIKE_COUNT + 1 
WHERE POST_NO = #{postNo}
